["Raid Target"] = "function (self, unitId, unitFrame, envTable, modTable)\n    \n    local reaction = UnitReaction(unitId, \"player\");\n    \n    if (reaction == 4) then\n        \n        --check of nameplate has raid marker\n        if (Plater.GetRaidMark (unitFrame)) and envTable.IsHidden then \n            envTable.ShowNameplate (unitFrame)\n        end\n        \n    end\n    \nend\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n",
["Nameplate Added"] = "function (self, unitId, unitFrame, envTable, modTable)\n    \n    local reaction = UnitReaction(unitId, \"player\");\n    \n    if (reaction == 4) then\n        \n        --check if NPC is using Plater's Soft Interact Icon and Name functionality\n        if unitFrame.isSoftInteract and unitFrame.PlateFrame.IsNpcWithoutHealthBar then\n            return\n        end\n        \n        --check if NPC should always be shown by npcID\n        for key, value in pairs(modTable.config.npcID) do\n            if (unitFrame.namePlateNpcId == tonumber(key)) then return end\n            if (string.lower(unitFrame.namePlateUnitName) == string.lower(value)) then return end\n        end\n        \n        --check if NPC should always be shown by auraID or name\n        for key, value in pairs(modTable.config.auraID) do\n            if Plater.NameplateHasAura (unitFrame, tonumber(key)) then return end\n            if Plater.NameplateHasAura (unitFrame, value) then return end\n        end\n        \n        --check if nameplate has raid marker\n        if (Plater.GetRaidMark (unitFrame)) then return end\n        \n        --check if the NPC has a custom color set in the 'Npc Colors and Names' table\n        if  (modTable.config['ShowCustomColorNpc']) then\n            local currentColor = Plater.GetNpcColor (unitFrame)\n            if currentColor then return end\n        end\n        \n        --check if nameplate is mouseover target\n        if UnitIsUnit(\"mouseover\",unitId) then \n            if envTable.IsHidden then envTable.ShowNameplate (unitFrame) end\n            return \n        end\n        \n        --check if a rare, elite or rareelite\n        if (modTable.config['ShowRareElite']) then \n            local plateFrame = C_NamePlate.GetNamePlateForUnit (unitId)\n            local unitClassification = UnitClassification (plateFrame.namePlateUnitToken)\n            if ((unitClassification == \"rareelite\") or (unitClassification == \"elite\") or (unitClassification == \"rare\")) then \n                if envTable.IsHidden then envTable.ShowNameplate (unitFrame) end\n                return \n            end\n        end\n        --check for hiding friendly NPCs\n        if (modTable.config['ShowFriendlyNPC']) then\n            if envTable.IsHidden then envTable.ShowNameplate (unitFrame) end\n            return \n        end\n        \n        --check if is only open world\n        if (modTable.config['OnlyInOpenWorld'] and Plater.ZoneInstanceType ~= \"none\") then\n            if envTable.IsHidden then envTable.ShowNameplate (unitFrame) end\n            return \n        end\n        \n        --check for only in combat\n        if (modTable.config['ShowInCombat'] and InCombatLockdown()) then\n            if envTable.IsHidden then envTable.ShowNameplate (unitFrame) end\n            return \n        end\n        \n        --check if part of a quest\n        if (unitFrame.namePlateIsQuestObjective) then\n            if envTable.IsHidden then envTable.ShowNameplate (unitFrame) end\n            return \n        end\n        \n        --check if current target\n        if (unitFrame.namePlateIsTarget) then\n            if envTable.IsHidden then envTable.ShowNameplate (unitFrame) end\n            return \n        end\n        \n        --check if casting an ability\n        if (UnitCastingInfo(unitId)) then\n            if envTable.IsHidden then envTable.ShowNameplate (unitFrame) end\n            return \n        end\n        \n        if envTable.IsHidden then Plater.ShowHealthBar (unitFrame) end\n        Plater.HideHealthBar (unitFrame, nil, modTable.config[\"showName\"])\n        Plater.DisableHighlight (unitFrame)\n        envTable.ShowName = modTable.config[\"showName\"]\n        envTable.IsHidden = true\n        \n    end\n    \nend\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n",
["Cast Start"] = "function (self, unitId, unitFrame, envTable, modTable, modTable)\n    \n    local reaction = UnitReaction(unitId, \"player\");\n    \n    if (reaction == 4) then\n        \n        --check if casting an ability\n        if envTable.IsHidden  then \n            envTable.ShowNameplate (unitFrame)\n        end\n        \n    end\n    \nend",
["Enter Combat"] = "function (self, unitId, unitFrame, envTable, modTable)\n    \n    local reaction = UnitReaction(unitId, \"player\");\n    \n    if (reaction == 4) then\n        \n        --check for only in combat\n        if (modTable.config['ShowInCombat']) and envTable.IsHidden then\n            envTable.ShowNameplate (unitFrame)\n        end\n        \n    end\n    \nend\n\n\n\n\n\n\n",
["Target Changed"] = "function (self, unitId, unitFrame, envTable, modTable)\n    \n    local reaction = UnitReaction(unitId, \"player\");\n    \n    if (reaction == 4) then\n        \n        --check if current target\n        if (unitFrame.namePlateIsTarget) and envTable.IsHidden then\n            envTable.ShowNameplate (unitFrame)\n        end\n        \n    end\n    \nend\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n",
["Nameplate Updated"] = "function (self, unitId, unitFrame, envTable, modTable)\n    \n    local reaction = UnitReaction(unitId, \"player\");\n    \n    if (reaction == 4) then\n        \n        --check if NPC is using Plater's Soft Interact Icon and Name functionality\n        if unitFrame.isSoftInteract and unitFrame.PlateFrame.IsNpcWithoutHealthBar then\n            return\n        end\n        \n        --check if NPC should always be shown by npcID\n        for key, value in pairs(modTable.config.npcID) do\n            if (unitFrame.namePlateNpcId == tonumber(key)) then return end\n            if (string.lower(unitFrame.namePlateUnitName) == string.lower(value)) then return end\n        end\n        \n        --check if NPC should always be shown by auraID or name\n        for key, value in pairs(modTable.config.auraID) do\n            if Plater.NameplateHasAura (unitFrame, tonumber(key)) then return end\n            if Plater.NameplateHasAura (unitFrame, value) then return end\n        end\n        \n        --check if nameplate has raid marker\n        if (Plater.GetRaidMark (unitFrame)) then return end\n        \n        --check if the NPC has a custom color set in the 'Npc Colors and Names' table\n        if  (modTable.config['ShowCustomColorNpc']) then\n            local currentColor = Plater.GetNpcColor (unitFrame)\n            if currentColor then return end\n        end\n        \n        --check if nameplate is mouseover target\n        if UnitIsUnit(\"mouseover\",unitId) then \n            if envTable.IsHidden then envTable.ShowNameplate (unitFrame) end\n            return \n        end\n        \n        --check if a rare, elite or rareelite\n        if (modTable.config['ShowRareElite']) then \n            local plateFrame = C_NamePlate.GetNamePlateForUnit (unitId)\n            local unitClassification = UnitClassification (plateFrame.namePlateUnitToken)\n            if ((unitClassification == \"rareelite\") or (unitClassification == \"elite\") or (unitClassification == \"rare\")) then \n                if envTable.IsHidden then envTable.ShowNameplate (unitFrame) end\n                return \n            end\n        end\n        --check for hiding friendly NPCs\n        if (modTable.config['ShowFriendlyNPC'] and unitFrame.PlayerCannotAttack) then\n            if envTable.IsHidden then envTable.ShowNameplate (unitFrame) end\n            return \n        end\n        \n        --check if is only open world\n        if (modTable.config['OnlyInOpenWorld'] and Plater.ZoneInstanceType ~= \"none\") then\n            if envTable.IsHidden then envTable.ShowNameplate (unitFrame) end\n            return \n        end\n        \n        --check for only in combat\n        if (modTable.config['ShowInCombat'] and InCombatLockdown()) then\n            if envTable.IsHidden then envTable.ShowNameplate (unitFrame) end\n            return \n        end\n        \n        --check if part of a quest\n        if (unitFrame.namePlateIsQuestObjective) then\n            if envTable.IsHidden then envTable.ShowNameplate (unitFrame) end\n            return \n        end\n        \n        --check if current target\n        if (unitFrame.namePlateIsTarget) then\n            if envTable.IsHidden then envTable.ShowNameplate (unitFrame) end\n            return \n        end\n        \n        --check if casting an ability\n        if (UnitCastingInfo(unitId)) then\n            if envTable.IsHidden then envTable.ShowNameplate (unitFrame) end\n            return \n        end\n        \n        if envTable.IsHidden then Plater.ShowHealthBar (unitFrame) end\n        Plater.HideHealthBar (unitFrame, nil, modTable.config[\"showName\"])\n        Plater.DisableHighlight (unitFrame)\n        envTable.ShowName = modTable.config[\"showName\"]\n        envTable.IsHidden = true\n        \n    end\n    \nend\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n",
["Initialization"] = "function (modTable)\n    \nend\n\n\n",
["Constructor"] = "function (self, unitId, unitFrame, envTable, modTable)\n    \n    --function to show the healthBar\n    function envTable.ShowNameplate (unitFrame)\n        if envTable.ShowName then \n            Plater.HideHealthBar (unitFrame) \n            envTable.ShowName = false \n        end\n        Plater.ShowHealthBar (unitFrame)\n        Plater.EnableHighlight (unitFrame)\n        envTable.IsHidden = false\n    end\n    \nend\n\n\n\n\n\n\n\n",
},
